export default function CourseListing() {
  const [filters, setFilters] = useState({
    certifier: '',
    search: ''
  });
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedCourse, setSelectedCourse] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const coursesPerPage = 12;
  const { t, language } = useLocalization();

  const coursesData = useMemo(() => {
    return language === 'en' ? COURSES_DATA_EN : COURSES_DATA;
  }, [language]);

  const filterOptions = useMemo(() => ({
    certifiers: [...new Set(coursesData.map(c => c["Ente Certificador"]).filter(Boolean))],
  }), [coursesData]);

  const filteredCourses = useMemo(() => {
    return coursesData.filter(course => {
      const matchesCertifier = filters.certifier === 'all' || !filters.certifier || course["Ente Certificador"] === filters.certifier;
      
      const matchesSearch = !filters.search || 
        // Search in course name (Certificaciones)
        course["Certificaciones"]?.toLowerCase().includes(filters.search.toLowerCase()) ||
        // Search in area
        course["Área"]?.toLowerCase().includes(filters.search.toLowerCase()) ||
        // Search in general objective
        course["Objetivo general"]?.toLowerCase().includes(filters.search.toLowerCase()) ||
        // Search in target profile
        course["Perfil de salida"]?.toLowerCase().includes(filters.search.toLowerCase()) ||
        // Search in contents
        course["Contenidos"]?.toLowerCase().includes(filters.search.toLowerCase()) ||
        // Search in certifier
        course["Ente Certificador"]?.toLowerCase().includes(filters.search.toLowerCase());

      return matchesCertifier && matchesSearch;
    });
  }, [coursesData, filters]);

  const totalPages = Math.ceil(filteredCourses.length / coursesPerPage);
  const startIndex = (currentPage - 1) * coursesPerPage;
  const displayedCourses = filteredCourses.slice(startIndex, startIndex + coursesPerPage);

  const handleBookSeat = (courseName) => {
    setSelectedCourse(courseName);
    setIsModalOpen(true);
  };

  const resetFilters = () => {
    setFilters({ certifier: '', search: '' });
    setCurrentPage(1);
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({ ...prev, [key]: value }));
    setCurrentPage(1);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-white">
      <div className="bg-white border-b">
        <div className="max-w-7xl mx-auto px-4 py-12">
          <h1 className="text-4xl font-bold mb-4">
            {t('courseListingTitle')} {filteredCourses.length}
          </h1>
          <p className="text-gray-600">
            {t('courseListingDescription')}
          </p>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 py-8">
        {/* Redesigned Filter Section */}
        <Card className="p-6 mb-8 shadow-sm border">
          <div className="flex flex-col lg:flex-row items-end gap-4">
            {/* Search Filter - Now takes more space */}
            <div className="flex-1 w-full">
              <Label className="text-sm font-medium mb-2 block text-gray-700">
                {t('searchCourses') || 'Search Courses'}
              </Label>
              <div className="relative">
                <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                <Input
                  type="text"
                  placeholder={t('searchPlaceholder') || 'Search by course name, area, description...'}
                  value={filters.search}
                  onChange={(e) => handleFilterChange('search', e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                />
              </div>
            </div>

            {/* Certifier Filter */}
            <div className="w-full lg:w-64">
              <Label className="text-sm font-medium mb-2 block text-gray-700">
                {t('certifier') || 'Certifier'}
              </Label>
              <Select 
                value={filters.certifier} 
                onValueChange={(value) => handleFilterChange('certifier', value)}
              >
                <SelectTrigger className="w-full border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent">
                  <SelectValue placeholder={t('selectCertifier') || 'All Certifiers'} />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">{t('allCertifiers') || 'All Certifiers'}</SelectItem>
                  {filterOptions.certifiers.map(certifier => (
                    <SelectItem key={certifier} value={certifier}>{certifier}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Reset Button */}
            <Button 
              onClick={resetFilters}
              variant="outline"
              className="whitespace-nowrap border-gray-300 text-gray-700 hover:bg-gray-50 hover:text-gray-900"
            >
              <RefreshCw className="h-4 w-4 mr-2" />
              {t('reset') || 'Reset'}
            </Button>
          </div>

          {/* Active Filters Display */}
          {(filters.certifier || filters.search) && (
            <div className="mt-4 flex flex-wrap gap-2">
              {filters.certifier && filters.certifier !== 'all' && (
                <div className="inline-flex items-center gap-1 bg-green-100 text-green-800 px-3 py-1 rounded-full text-sm">
                  Certifier: {filters.certifier}
                  <button 
                    onClick={() => handleFilterChange('certifier', '')}
                    className="hover:text-green-900 focus:outline-none"
                  >
                    <X className="h-3 w-3" />
                  </button>
                </div>
              )}
              {filters.search && (
                <div className="inline-flex items-center gap-1 bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm">
                  Search: "{filters.search}"
                  <button 
                    onClick={() => handleFilterChange('search', '')}
                    className="hover:text-blue-900 focus:outline-none"
                  >
                    <X className="h-3 w-3" />
                  </button>
                </div>
              )}
            </div>
          )}
        </Card>

        {/* Results Count */}
        <div className="mb-6">
          <p className="text-gray-600">
            Showing {displayedCourses.length} of {filteredCourses.length} courses
            {filters.search && ` for "${filters.search}"`}
            {filters.certifier && filters.certifier !== 'all' && ` by ${filters.certifier}`}
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6 mb-8">
          {displayedCourses.length > 0 ? (
            displayedCourses.map((course, index) => (
              <CourseCard 
                key={index} 
                course={course}
                onBookSeat={handleBookSeat}
              />
            ))
          ) : (
            <div className="col-span-full text-center py-12">
              <div className="text-gray-400 mb-4">
                <Search className="h-12 w-12 mx-auto" />
              </div>
              <h3 className="text-lg font-medium text-gray-900 mb-2">No courses found</h3>
              <p className="text-gray-500">
                Try adjusting your search terms or filters to find what you're looking for.
              </p>
              <Button 
                onClick={resetFilters}
                variant="outline"
                className="mt-4"
              >
                Clear all filters
              </Button>
            </div>
          )}
        </div>

        {totalPages > 1 && displayedCourses.length > 0 && (
          <div className="flex justify-center gap-2">
            <Button
              variant="outline"
              onClick={() => setCurrentPage(p => Math.max(1, p - 1))}
              disabled={currentPage === 1}
            >
              {t('prev') || 'Previous'}
            </Button>
            
            {[...Array(totalPages)].map((_, i) => (
              <Button
                key={i}
                variant={currentPage === i + 1 ? "default" : "outline"}
                onClick={() => setCurrentPage(i + 1)}
                className={currentPage === i + 1 ? "bg-purple-600" : ""}
              >
                {String(i + 1).padStart(2, '0')}
              </Button>
            ))}
            
            <Button
              variant="outline"
              onClick={() => setCurrentPage(p => Math.min(totalPages, p + 1))}
              disabled={currentPage === totalPages}
            >
              {t('next') || 'Next'}
            </Button>
          </div>
        )}
      </div>

      <BookingModal 
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        courseName={selectedCourse}
      />

      <style jsx global>{`
        @keyframes scale-in {
          from {
            transform: scale(0);
          }
          to {
            transform: scale(1);
          }
        }
        .animate-scale-in {
          animation: scale-in 0.5s ease;
        }
      `}</style>
    </div>
  );
}